
//
fetchApi(`${basePath()}/ajaxResponsePessoasValor.php`)
.then(data => { 
    var dataNome = [], dataValor = [];
    for(let i in data) { dataNome.push(data[i].nome); dataValor.push(parseFloat(data[i].valor)); }
    var dataValorTotal = data.reduce(function(sum, item){ return sum = sum + parseFloat(item.valor); },0);

    console.log(dataValor)
    var dashChart = document.getElementById('chart').getContext('2d');
    var chart = new Chart(dashChart, {
        type: 'bar',
        data: {
            labels: dataNome,
            datasets: [{
                label: `Geral: R$ ${dinheiroFormatoBrl(dataValorTotal.toFixed(2))} - Individual R$`,
                data: dataValor,
                backgroundColor: [
                    'rgba(255, 99, 132, 0.2)',
                    'rgba(54, 162, 235, 0.2)',
                    'rgba(255, 206, 86, 0.2)',
                    'rgba(75, 192, 192, 0.2)',
                    'rgba(153, 102, 255, 0.2)',
                    'rgba(255, 159, 64, 0.2)'
                ],
                borderColor: [
                    'rgba(255, 99, 132, 1)',
                    'rgba(54, 162, 235, 1)',
                    'rgba(255, 206, 86, 1)',
                    'rgba(75, 192, 192, 1)',
                    'rgba(153, 102, 255, 1)',
                    'rgba(255, 159, 64, 1)'
                ],
                borderWidth: 1
            }]
        },
        options: {
            scales: {
                y: {
                    beginAtZero: true
                }
            }
        }
    });

});