
// Scripts JS

// Mascara Dt Nascimento
new IMask(document.getElementById('dt_nascimento'), {
    mask: '00/00/0000'
});

// CPF e CNPJ - Mascara tanto CPF como CNPJ
document.getElementById('cpf_cnpj').addEventListener('focusin', function(e) {
    let val = e.target.value;
    val.unmaskedValue; // Remover mascara
    val = val.split(".").join("");
	val = val.split("-").join("");
	val = val.split("/").join("");
    return this.value = val;
});

document.getElementById('cpf_cnpj').addEventListener('focusout', function(e) {
    let maskCpfCnpj;
    if(this.value.length == 11) {
        maskCpfCnpj = IMask(document.getElementById('cpf_cnpj'), {
            mask: '000.000.000-00'
        });
    } else if(this.value.length == 14) {
        maskCpfCnpj = IMask(document.getElementById('cpf_cnpj'), {
            mask: '00.000.000/0000-00'
        });
    } else {
        this.value.unmaskedValue
        //maskCpfCnpj.updateValue();
    }
});

